import React, { useState } from "react";

import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";

const NewExpense = (props) => {
  const [isAdding, setIsAdding] = useState(false);
  const savedExpensedData = (enteredExpenseData) => {
    const expensedData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    props.addExpense(expensedData);
  };
  const startAdding = () => {
    setIsAdding(true);
  };

  const stopAdding = () => {
    setIsAdding(false);
  };

  let ui = <button onClick={startAdding}>Add New Expense</button>;
  if (!isAdding) {
    return (ui = (
      <ExpenseForm
        onSaveExpenseData={savedExpensedData}
        onCancel={stopAdding}
      />
    ));
  }
  return <div className="new-expense"> {ui}</div>;
};
export default NewExpense;
